function getRandomEmotions() {
	a = "0123456";
	r = ""
	for (var i = 0 ; i < 22 ; i++)
		r += a[Math.floor(Math.random()*a.length)];
	return r;
}

function fillNumber(number, length) {

	length = length || 6;
	number = number || 0;

	var str = String(number),
		result = Array(length).fill(0).join("");

	result = result + str;

	return result.substring(result.length - length, result.length);
}

